public class desafio001 {

	public static void main(String[] args) {
		// TODO Auto-generated methodSystem.out.println

		
			/* Variables Primitivas en Java
			   Tipo numerico entero
			   byte: 8 bits de almacenamiento | valores en el rango de [-128, 127]
			*/
			byte unByte = 8;
			System.out.println(unByte);

			//short: 2 bytes de almacenamiento | valores en el rango de [-32.768, 32.767]
			short unShort = 11589;
			System.out.println(unShort);

			//int
			int unInt = 12;
			System.out.println(unInt);

			//long
			long unLong = 1000000000;
			System.out.println(unLong);

			//Tipo numerico decimal
			//Float
			float unFloat = 10;
			System.out.println(unFloat);
			
			//double
			double unDouble = 1020.3265;
			System.out.println(unDouble);

			//Booleanos y caracteres
			//boolean
			boolean unBoolean = true;
			System.out.println(unBoolean);

			//string
			String unString = "Hola Mundo";
			System.out.println(unString);
	}
}
